template<typename Number>
Number* binary_search(Number* first, Number* last, Number value){
    Number* ResultPoint = nullptr;
    while (first <= last) {
        Number* mid = first + (last - first) / 2;
        if ((*mid) == value) {
            ResultPoint = mid;
            break;
        } else if ((*mid) > value) {
            last = mid - 1;
        } else  {
            first = mid + 1;
        }
    }
    return ResultPoint;
}



