```c++
#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main(){
    string filename, searchword;
    cout << "Enter filename: ";
    cin >> filename;
    cout << "Enter word to search for: ";
    cin >> searchword;

    ifstream file(filename);//открываем файл для чтения

    if(!file.is_open()){
        cout << "Error opening file " << filename << endl;
        return 1;//если файл не открылся выходим из программы с ошибкой
    }

    int count = 0;//счетчик вхождений слова

    string line;
    while(getline(file, line)){//читаем файл построчно
        size_t pos = 0;
        while((pos = line.find(searchword, pos)) != string::npos){//находим вхождения слова в строку
            count++;
            pos += searchword.length();//для поиска следующего вхождения продолжаем поиск после найденного слова
        }
    }

    cout << "Number of occurrences of " << searchword << " in file " << filename << " is " << count << endl;

    file.close();//закрываем файл
    return 0;
}
