public static boolean isPowerOfThree(int n) {
    if (n == 0 || n == 1 || n == 2) {
        return true;
    }

    int x = 1;
    while (x * x <= n) {
        if (n % x == 0) {
            return true;
        }
        x++;
    }
    return false;
}
