public class Solution {
    public boolean isPowerOfThree(int n) {
        if (n < 0 || n > 231) {
            return false;
        }
        int count = 0;
        while (n % 3 == 0) {
            n /= 3;
            count++;
        }
        return count == 0;
    }
}
