public class NumberIsPowerOfThree {

    public static boolean isPowerOfThree(int n) {
        if (n == 0) {
            return false;
        }

        int x = 3;
        while (n % x != 0) {
          x++;
        }
        return n == 3 * x;
    }

    public static void main(String[] args) {
        int n = Integer.parseInt(args[0]);
        System.out.println(isPowerOfThree(n));
    }
}
