 def find_longest_common_subsequence(s1, s2):
    n = len(s1)
    m = len(s2)
    if n == 0 or m == 0:
        return 0

    matrix = [[0 for _ in range(n + 1)] for _ in range(m + 1)]
    matrix[0][0] = 1

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if s1[i - 1] == s2[j - 1]:
                matrix[i][j] = matrix[i - 1][j - 1] + 1
            else:
                break

    return matrix[n][m]

