def longest_common_subsequence(str1, str2):
    n = len(str1) + len(str2) - 1
    dp = [[0]*(n+1)] * (n+1)
    for i in range(n):
        dp[i][0] = i
        for j in range(i+1, n):
            if str1[j-1] == str2[i]:
                dp[i][j] = dp[i-1][j-1]+1
            else:
                dp[i][j], dp[i-1][j] = dp[i][j-1], dp[i-1][ j ]
    return dp[n-1][n-1]

