class Queue:
    def __init__(self):
        self.items = []
        self.max_size = 10

    def enqueue(self, item):
        if len(self.items) == self.max_size:
            raise OverflowError("Queue is full")
        self.items.append(item)

    def dequeue(self):
        if not self.items:
            raise EmptyQueueError("Queue is empty")
        return self.items.pop()

    def size(self):
        return len(self.items)
